import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

export default function Detail () {
  const [data, setdata] = useState(null);
  const { id } = useParams();
  const history = useHistory();
  const DeleteImage = async () => {
    const res = await fetch(`https://backend-lobrockyl.herokuapp.com/delete/${id}`, { method: 'DELETE' })
    const result = await res.json();
    if (result.success) history.push('/');
  }

  useEffect(() => {
    FetchData();
  }, [])

  const FetchData = async () => {
    if (!id) return;
    const res = await fetch(`https://backend-lobrockyl.herokuapp.com/show/${id}`);
    const result = await res.json();
    if (result.success) setdata(result.image);
  }

  return (
      <>
    { data && <div className="flex justify-center w-100 p-4 border-2 min-h-screen">
      <div>
        <div className='m-4 flex justify-end items-center cursor-pointer'>
            <EditIcon onClick={() => history.push(`/edit/${id}`)}/>
            <DeleteIcon onClick={() => DeleteImage()}/>
          </div>
        <h1 className="text-4xl text-gray-500 font-semibold my-4 text-center">{data.ImgName}</h1>
        <img className="object-fill h-1/3 w-4/5 outline-none border-none border-2" src={data.ImgURL} alt="imgg"/>
        <p className="text-md my-8 text-gray-700">{data.ImgDetails}</p>
        </div>
    </div>}
    {!data && <div className='flex justify-center w-full text-gray-500 text-2xl font-bold'>No data Found!</div>}
    </>
  )
}
