/* eslint no-undef: 0 */ // --> OFF

import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { useHistory } from 'react-router-dom';

// eslint-disable-next-line react/prop-types
export default function ActionAreaCard ({ id, name, image, detail }) {
  const history = useHistory();

  const DeleteImage = async () => {
    const res = await fetch(`https://backend-lobrockyl.herokuapp.com/delete/${id}`, { method: 'DELETE' })
    const result = await res.json();
    if (result.success) window.location.reload();
  }
  // const Style = {
  //   height: 32,
  // };
  return (
    <div className={'relative m-2'}>
      <div onClick={() => history.push(`/details/${id}`)}><Card sx={{ maxWidth: 345 }}>
        <CardActionArea>
          <CardMedia
            component="img"
            height= '40'
            className={classes.media}
            image={image}
            alt="image"
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {detail}
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
      </div>
      <div className='absolute bottom-1 right-1 flex justify-end items-center z-50 cursor-pointer'>
        <EditIcon onClick={() => history.push(`/edit/${id}`)} />
        <DeleteIcon onClick={() => DeleteImage()} />
      </div>
    </div>
  );
}
