import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';

export default function Detail () {
  const [data, setdata] = useState(null);
  const { id } = useParams();
  const history = useHistory();
  const DeleteImage = async () => {
    const res = await fetch(`https://backend-lobrockyl.herokuapp.com/delete/${id}`, { method: 'DELETE' })
    const result = await res.json();
    if (result.success) history.push('/');
  }

  useEffect(() => {
    FetchData();
  }, [])

  const FetchData = async () => {
    if (!id) return;
    const res = await fetch(`https://backend-lobrockyl.herokuapp.com/show/${id}`);
    const result = await res.json();
    if (result.success) setdata(result.image);
  }

  return (
      <>
    { data && <div className="flex justify-center w-100 p-4 border-2 min-h-screen">
    <Card sx={{ maxWidth: 345 }}>
        <CardActionArea>
          <div className = "h-2/3 object-fit">
            <CardMedia
              component="img"
              height= '140'
              image={data.ImgURL}
              alt="image"
            />
          </div>
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {data.ImgName}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {data.ImgDetails}
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
      <div className='absolute bottom-1 right-8 flex justify-end items-center z-50 cursor-pointer'>
        <EditIcon onClick={() => history.push(`/edit/${id}`)} />
        <DeleteIcon onClick={() => DeleteImage()} />
      </div>
    </div>
    }
    {!data && <div className='flex justify-center w-full text-gray-500 text-2xl font-bold'>No data Found!</div>}
    </>
  )
}
